mapper.select:
SELECT APLCNT_IDX , RECEIPT_DATE , FN_GET_BIZAREA(APLCNT_BIZAREA) APLCNT_BIZAREA , FN_GET_DEPT(APLCNT_DEPT) APLCNT_DEPT , APLCNT_CLASS , EMPLY_NUM , APLCNT_NM , BIRTHDAY , FN_CMM_NM('SEX', APLCNT_SEX) APLCNT_SEX , CONTACT_INFO FROM LBAPLCNTLIST
WHERE EMPLY_NUM = #{emplyNum}
AND APLCNT_IDX != #{aplcntIdx}
ORDER BY APLCNT_IDX DESC

SELECT APLCNT_IDX , RECEIPT_DATE , FN_GET_BIZAREA(APLCNT_BIZAREA) APLCNT_BIZAREA , FN_GET_DEPT(APLCNT_DEPT) APLCNT_DEPT , APLCNT_CLASS , EMPLY_NUM , APLCNT_NM , BIRTHDAY , FN_CMM_NM('SEX', APLCNT_SEX) APLCNT_SEX , CONTACT_INFO , EMAIL , FN_CMM_NM('OXSTTS', BLACKLIST) BLACKLIST FROM LBAPLCNTLIST
WHERE APLCNT_NM = #{aplcntNm} AND BIRTHDAY LIKE #{birthday} || '%' AND APLCNT_SEX = #{aplcntSex}
AND APLCNT_IDX != #{aplcntIdx}
ORDER BY APLCNT_IDX DESC


SELECT
	COUNT(APLCNT_IDX) TOTAL_COUNT
FROM LBAPLCNTLIST
WHERE APLCNT_IDX > 0


	AND APLCNT_BIZAREA = #{srchBizarea}


	AND APLCNT_DEPT = #{srchDept}


	AND APLCNT_CLASS = #{srchAplcntClass}


	AND PHONE_ITVW_RESULT = #{srchPhoneItvwResult}


	AND ITVW_DATE = #{srchItvwDate}


	AND ITVW_RESULT = #{srchItvwResult}


	AND APLCNT_NM = #{srchAplcntNm}


	AND BIRTHDAY LIKE #{srchBirthdayY} || '%'


	AND CONTACT_INFO LIKE '%'||#{srchContactInfo}||'%'


	AND (INID = #{srchUserId} OR UPID = #{srchUserId})

SELECT * FROM ( SELECT /*+ INDEX DESC (LBAPLCNTLIST LBAPLCNTLIST_PK) */ ROWNUM RN , APLCNT_IDX , RECEIPT_DATE , FN_GET_BIZAREA(APLCNT_BIZAREA) APLCNT_BIZAREA , FN_GET_DEPT(APLCNT_DEPT) APLCNT_DEPT , APLCNT_CLASS , EMPLY_NUM , APLCNT_NM , BIRTHDAY , FN_CMM_NM('SEX', APLCNT_SEX) APLCNT_SEX , FN_CMM_ADDR(ADDR_DO) ADDR_DO_NM , FN_CMM_ADDR(ADDR_SI) ADDR_SI_NM , FN_CMM_ADDR(ADDR_GU) ADDR_GU_NM , CONTACT_INFO , EMAIL , FN_CMM_NM('OXSTTS', MARITAL_STTS) MARITAL_STTS , FN_CMM_NM('HIGHESTEDU', HIGHEST_EDU) HIGHEST_EDU , MAJOR , FN_CMM_NM('OXSTTS', CAREER_STTS) CAREER_STTS , COMPANY1 , COMPANY_PERIOD1 , ASSIGNED_TASK1 , COMPANY2 , COMPANY_PERIOD2 , ASSIGNED_TASK2 , FN_CMM_NM('OXSTTS', BLACKLIST) BLACKLIST , FN_CMM_NM('RECRUITSITE1', RECRUIT_SITE1) RECRUIT_SITE1 , FN_GET_RCS2('RECRUITSITE2', RECRUIT_SITE2, RECRUIT_SITE1) RECRUIT_SITE2 , DOC_ITVW , PHONE_ITVW , FN_CMM_NM('PHONEITVWRESULT', PHONE_ITVW_RESULT) PHONE_ITVW_RESULT , PTOP_ITVW , INTERVIEWER , ITVW_DATE , ITVW_TIME , FN_CMM_NM('ATTDSTTS', ITVW_ATTD) ITVW_ATTD , FN_CMM_NM('ITVWRESULT', ITVW_RESULT) ITVW_RESULT , FN_CMM_NM('ATTDSTTS', EDU_ATTD_ITTN_STTS) EDU_ATTD_ITTN_STTS , FN_CMM_NM('ATTDSTTS', EDU_ATTD_STTS) EDU_ATTD_STTS , EDU_DATE , JOB_DAY , PUT_DAY , QUIT_DAY , INID , TO_CHAR(INDT, 'YYYYMMDD') INDT , UPID , TO_CHAR(UPDT, 'YYYYMMDD') UPDT FROM LBAPLCNTLIST WHERE 1=1
AND APLCNT_IDX > 0
AND APLCNT_BIZAREA = #{srchBizarea}
AND APLCNT_DEPT = #{srchDept}
AND APLCNT_CLASS = #{srchAplcntClass}
AND PHONE_ITVW_RESULT = #{srchPhoneItvwResult}
AND ITVW_DATE = #{srchItvwDate}
AND ITVW_RESULT = #{srchItvwResult}
AND APLCNT_NM = #{srchAplcntNm}
AND BIRTHDAY LIKE #{srchBirthdayY} || '%'
AND CONTACT_INFO LIKE '%'||#{srchContactInfo}||'%'
AND (INID = #{srchUserId} OR UPID = #{srchUserId})
AND ROWNUM <= #{page} * #{perPageNum} ) WHERE RN > ((#{page}-1) * #{perPageNum})

